import React, { useState, useEffect } from 'react';
import { Transition } from '@headlessui/react';
import sunset1 from '../image/sunset1.jpg';

function Habilidades({ seccionAnimacion }) {
  const [isMobile, setIsMobile] = useState(window.innerWidth < 768);

  useEffect(() => {
    const handleResize = () => {
      setIsMobile(window.innerWidth < 768);
    };

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <>
      <Transition
        show={seccionAnimacion === 'Habilidades'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform -translate-y-full"
        enterTo="opacity-100 transform translate-y-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-y-0"
        leaveTo="opacity-0 transform -translate-y-full"
      >
        <div
          id="Habilidades"
          className="h-full flex flex-col items-center justify-center bg-gradient-to-b from-indigo-800 to-purple-800 text-white"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${sunset1})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
            minHeight: isMobile ? '150vh' : '100vh'
          }}
        >
          <div className="max-w-5xl w-full px-8">
            <div className="flex flex-col items-center p-2">
              <h2 className="text-2xl md:text-3xl lg:text-4xl font-bold mb-4">Habilidades Técnicas de Producción</h2>

              {/* Cameras */}
              <div className="flex items-center space-x-4 my-4">
                <img src={sunset1} alt="Camera Icon" className="w-8 h-8 md:w-10 md:h-10" />
                <p>Manejo de cámaras DSLR y cámaras de video profesionales, incluyendo ajustes de exposición, enfoque, balance de blancos y composición de tomas.</p>
              </div>

              {/* Lighting */}
              <div className="flex items-center space-x-4 my-4">
                <img src={sunset1} alt="Light Icon" className="w-8 h-8 md:w-10 md:h-10" />
                <p>Configuración y manejo de equipos de iluminación para diferentes entornos y situaciones, tanto en interiores como en exteriores.</p>
              </div>

              {/* Sound */}
              <div className="flex items-center space-x-4 my-4">
                <img src={sunset1} alt="Sound Icon" className="w-8 h-8 md:w-10 md:h-10" />
                <p>Captura y edición de sonido, manejo de micrófonos y equipos de grabación, y técnicas de postproducción para asegurar una alta calidad de audio.</p>
              </div>

              {/* Adobe Software */}
              <h2 className="text-2xl md:text-3xl lg:text-4xl font-bold my-8">Software</h2>

              <div className="grid grid-cols-2 gap-4 md:gap-8">
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Photoshop Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Edición de imágenes y creación de gráficos visuales, retoque fotográfico, y diseño de elementos visuales para proyectos audiovisuales.</p>
                </div>
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Premiere Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Edición de video no lineal, montaje, corrección de color, y exportación de proyectos en varios formatos y resoluciones.</p>
                </div>
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="After Effects Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Creación de efectos visuales, animación gráfica, y composición digital para integrar elementos visuales complejos en los proyectos.</p>
                </div>
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Lightroom Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Edición y retoque fotográfico, gestión de catálogos de imágenes, y aplicación de ajustes avanzados para la corrección y mejora de fotos.</p>
                </div>
              </div>

              {/* Roles en Producción */}
              <h2 className="text-2xl md:text-3xl lg:text-4xl font-bold my-8">Roles en Producción</h2>

              <div className="grid grid-cols-2 gap-4 md:gap-8">
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Producer Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Coordinación y gestión de todos los aspectos de la producción, desde la preproducción hasta la postproducción, asegurando el cumplimiento de plazos y presupuestos.</p>
                </div>
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Director Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Dirección de proyectos audiovisuales, supervisión del equipo técnico y artístico, y toma de decisiones creativas para garantizar la visión artística del proyecto.</p>
                </div>
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Writer Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Escritura de guiones originales, desarrollo de tramas y personajes, y creación de diálogos coherentes y atractivos para diversos formatos audiovisuales.</p>
                </div>
                <div className="flex items-center space-x-4 my-4">
                  <img src={sunset1} alt="Continuity Icon" className="w-8 h-8 md:w-10 md:h-10" />
                  <p>Supervisión de la continuidad en las grabaciones, asegurando la coherencia visual y narrativa entre las diferentes tomas y escenas.</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </Transition>
    </>
  );
}

export default Habilidades;
