import React from 'react';
import { Transition } from '@headlessui/react';
import street from '../image/street.jpg';
import yo from '../image/yo.jpg';
import gitHub from '../image/gitHub.png';
import linkedin from '../image/linkedin.png';
import instagram from '../image/instagram.png';

function Inicio({ seccionAnimacion, navegacion }) {

  const descargarCV = () => {
    const link = document.createElement('a');
    link.href = process.env.PUBLIC_URL + '/CV-CarmeloRodriguez.pdf';
    link.download = 'CV-CarmeloRodriguez.pdf';
    link.click();
  };

  return (
    <>
      <Transition
        show={seccionAnimacion === 'Inicio'}
        enter="transition ease-out duration-500"
        enterFrom="opacity-0 transform -translate-x-full"
        enterTo="opacity-100 transform translate-x-0"
        leave="transition ease-in duration-300"
        leaveFrom="opacity-100 transform translate-x-0"
        leaveTo="opacity-0 transform -translate-x-full"
      >
        <div
          id="Inicio"
          className="h-screen flex flex-col items-center justify-center"
          style={{
            backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.6), rgba(0, 0, 0, 0.9)), url(${street})`,
            backgroundSize: 'cover',
            backgroundPosition: 'center',
          }}
        >
          <div className='flex flex-col md:flex-row'>
            <div className='flex-grow md:w-3/4 flex flex-col justify-center p-4'>
              <h2 className="text-4xl font-base text-white text-center md:text-left">
                ¡Hola, soy <span className='font-extrabold'>Carmelo Rodríguez</span>!
              </h2>
              <h2 className='text-3xl font-bold text-white text-center md:text-left mt-2'>Frontend Developer</h2>
              <p className='text-white mt-3 text-justify max-w-3xl'>
                Desde temprana edad, descubrí mi pasión por el arte y la creatividad. Esta inclinación artística me ha
                acompañado a lo largo de los años y la he canalizado hacia el diseño web, fusionando estética y funcionalidad
                en cada proyecto. Mi formación en Desarrollo de Aplicaciones Web, complementada con un fuerte enfoque en
                diseño, me permite crear experiencias digitales que no solo son visualmente atractivas, sino también intuitivas
                y eficientes. Nacido en Huelva, España, mi objetivo es aprovechar mi creatividad innata para desarrollar soluciones
                web innovadoras y de alta calidad.
              </p>
              <div className='flex flex-wrap justify-center md:justify-start'>
                <a href="https://github.com/crodros2601" target="_blank" rel="noopener noreferrer"><img src={gitHub} className='w-12 h-auto mt-3 mx-2' alt="Icono de GitHub" /></a>
                <a href="https://www.linkedin.com/in/carmelorodriguezrosalina/" target="_blank" rel="noopener noreferrer">
                <img src={linkedin} className='w-10 h-auto mt-3 mx-2' alt="Icono de Linkedin" /></a>
                <a href="https://www.instagram.com/karmelartem" target="_blank" rel="noopener noreferrer"><img src={instagram} className='w-8 h-auto mt-3 mx-2' alt="Icono de Instagram" /></a>
                <button className='w-24 p-1 mt-8 mx-2 bg-white rounded-full font-extrabold' onClick={descargarCV}>CV</button>
                <button onClick={() => navegacion("Contacto")} className='w-32 p-1 mt-8 mx-2 bg-white rounded-full font-extrabold'>CONTACTO</button>
              </div>
            </div>
            <div className='flex-none md:w-1/4 flex items-center justify-center p-4'>
              <img src={yo} className='rounded-full w-48 h-48 md:w-60 md:h-60 object-cover hover:scale-110' alt="Imagen de Carmelo Rodríguez" />
            </div>
          </div>
        </div>
      </Transition>
    </>
  );
}

export default Inicio;
